#include "Wolf.h"
#include "World.h"

Wolf::Wolf(float x, float y) : Animal(x, y) {
    this->hunger = max_hunger;
    this->target_rabbit = NULL;
    this->type = WOLF;
    this->rect = FloatRect(0, 0, 70, 70);
}

bool Wolf::find_rabbit() {
    Entity* entity;
    for (int i = 0; i < world.get_entity_num(RABBIT); i++) {
        entity = world.get_entity(i, RABBIT);
        // check rabbit 
        if (this->distance(*entity) < detect_range) {
            target_rabbit = (Animal*)(entity);
            return true;
        }
    }
    return false;
}

bool Wolf::follow(int dt) {
    float distance = this->distance(*target_rabbit);
    target = target_rabbit->getPos();
    if (distance > 600) { move(dt); }
    else { move(dt * walk_mult); }
    
    if (target_rabbit->get_state() == RUNNING_AWAY) { return true; }
    else { return false; }
}

bool Wolf::hunt(int dt) {
    float distance = this->distance(*target_rabbit);
    if (distance > 10) {
        target = target_rabbit->getPos();
        move(dt * run_mult);
        return false;
    }
    else {
        delete target_rabbit;
        target_rabbit = NULL;
        hunger += 5000;
        return true;
    }
}

void Wolf::draw() {
    char* curr_arr = NULL;
    check_dir();
    switch (state) {
    case IDLE:
        if (direction == RIGHT)
        {
            curr_arr = (char*)(wolf_right_stand);
            draw_arr(curr_arr, 62, 50);
        }
        else if (direction == LEFT)
        {
            curr_arr = (char*)(wolf_left_stand);
            draw_arr(curr_arr, 62, 50);
        }
        else if (direction == TOP)
        {
            curr_arr = (char*)(wolf_front1);
            draw_arr(curr_arr, 30, 70);
        }
        else if (direction == DOWN)
        {
            curr_arr = (char*)(wolf_back1);
            draw_arr(curr_arr, 30, 70);
        }
        break;
    default:
        if (direction == RIGHT)
        {
            if (is_walk) {
                curr_arr = (char*)(wolf_right_move1);
            }
            else {
                curr_arr = (char*)(wolf_right_move2);
            }
            draw_arr(curr_arr, 70, 50);
        }
        else if (direction == LEFT)
        {
            if (is_walk) {
                curr_arr = (char*)(wolf_left_move1);
            }
            else {
                curr_arr = (char*)(wolf_left_move2);
            }
            draw_arr(curr_arr, 70, 50);
        }
        else if (direction == TOP)
        {
            if (is_walk) {
                curr_arr = (char*)(wolf_front1);
            }
            else {
                curr_arr = (char*)(wolf_front2);
            }
            draw_arr(curr_arr, 30, 70);
        }
        else if (direction == DOWN)
        {
            if (is_walk) {
                curr_arr = (char*)(wolf_back1);
            }
            else {
                curr_arr = (char*)(wolf_back2);
            }
            draw_arr(curr_arr, 30, 70);
        }
        break;
    }
    
    /*
    sf::CircleShape shape_red_c(10);
    shape_red_c.setRadius(detect_range);
    shape_red_c.setFillColor(sf::Color(0, 0, 0, 0));
    shape_red_c.setOutlineColor(sf::Color::Red);
    shape_red_c.setOutlineThickness(4);
    shape_red_c.setPosition(pos.x - detect_range, pos.y - detect_range);
    world.window->draw(shape_red_c);
    */
    
    sf::CircleShape shape_red_c(10);
    shape_red_c.setRadius(300.0);
    shape_red_c.setFillColor(sf::Color(0, 0, 0, 0));
    shape_red_c.setOutlineColor(sf::Color::Red);
    shape_red_c.setOutlineThickness(4);
    shape_red_c.setPosition(pos.x - 300.0, pos.y - 300.0);
    world.window->draw(shape_red_c);
}

void  Wolf::update(int dt) {
    hunger -= dt;
    if (hunger < 3000) {
        if (target_rabbit == NULL) {
            if (find_rabbit()) {
                state = FOLLOWING;
            }
        }
    }

    switch (state) {
    case IDLE:
        temp += dt;
        if (temp >= 2000) {
            //set random target
            temp = 0;
            target = Vector2f(pos.x + rand() % 500 - 250, pos.y + rand() % 500 - 250);
            state = MOVING;
        }
        break;
    case MOVING:
        walk_frame += dt;
        if (walk_frame > 400) {
            walk_frame = 0;
            is_walk = !is_walk;
        }
        move(dt); 
        break;
    case FOLLOWING:
        walk_frame += dt;
        if (walk_frame > 600) {
            walk_frame = 0;
            is_walk = !is_walk;
        }
        if (follow(dt)) {
            state = HUNTING;
        }
        break;
    case HUNTING:
        walk_frame += dt;
        if (walk_frame > 200) {
            walk_frame = 0;
            is_walk = !is_walk;
        }
        if (hunt(dt)) {
            walk_frame = 0;
            state = IDLE;
        }
        break;
    }
}

int Wolf::get_type() {
    return WOLF;
}

float Wolf::get_speed() {
    return speed;
}

